// 重写默认的app.js
import App from "next/app";
// 会引入全部的CSS，但同样能够异步分模块加载
import "antd/dist/antd.css";
import React from "react";
class CustomApp extends App {
  render() {
    // 每个页面
    const { Component } = this.props;
    console.log(Component);
    return (
      <>
        <Component />
      </>
    );
  }
}

export default CustomApp;
